'use client';
import React, { createContext, useState, useEffect, useRef, useContext, ReactNode } from 'react';

type TimerContextType = {
  mode: 'work' | 'break';
  timeRemaining: number;
  isActive: boolean;
  toggleTimer: () => void;
  resetTimer: () => void;
};

const TimerContext = createContext<TimerContextType | undefined>(undefined);

const WORK_MINUTES = 25;
const BREAK_MINUTES = 5;

export function TimerProvider({ children }: { children: ReactNode }) {
  const [mode, setMode] = useState<'work' | 'break'>('work');
  const [timeRemaining, setTimeRemaining] = useState(WORK_MINUTES * 60);
  const [isActive, setIsActive] = useState(false);
  const intervalRef = useRef<NodeJS.Timeout | null>(null);
  const audioRef = useRef<HTMLAudioElement | null>(null);

  useEffect(() => {
    // Initialize audio only on the client side
    if (typeof window !== 'undefined') {
      audioRef.current = new Audio("data:audio/mp3;base64,-x264");
    }
  }, []);

  useEffect(() => {
    if (isActive) {
      intervalRef.current = setInterval(() => {
        setTimeRemaining(prev => prev > 0 ? prev - 1 : 0);
      }, 1000);
    } else if (intervalRef.current) {
      clearInterval(intervalRef.current);
    }
    return () => {
      if (intervalRef.current) clearInterval(intervalRef.current);
    };
  }, [isActive]);

  useEffect(() => {
    if (timeRemaining === 0 && isActive) {
      audioRef.current?.play();
      const nextMode = mode === 'work' ? 'break' : 'work';
      setMode(nextMode);
      setTimeRemaining(nextMode === 'work' ? WORK_MINUTES * 60 : BREAK_MINUTES * 60);
    }
  }, [timeRemaining, mode, isActive]);

  const toggleTimer = () => setIsActive(!isActive);

  const resetTimer = () => {
    setIsActive(false);
    setMode('work');
    setTimeRemaining(WORK_MINUTES * 60);
  };

  return (
    <TimerContext.Provider value={{ mode, timeRemaining, isActive, toggleTimer, resetTimer }}>
      {children}
    </TimerContext.Provider>
  );
}

export function useTimer() {
  const context = useContext(TimerContext);
  if (context === undefined) {
    throw new Error('useTimer must be used within a TimerProvider');
  }
  return context;
}
