'use client';
import { useState, useEffect, useRef } from 'react';
import { Play, Pause, RotateCcw } from 'lucide-react';

// Audio for the timer completion alert
const completionSound = typeof window !== 'undefined' ? new Audio("data:audio/mp3;base64,-x264") : undefined;

const WORK_MINUTES = 25;
const BREAK_MINUTES = 5;

export default function PomodoroTimer() {
  const [mode, setMode] = useState<'work' | 'break'>('work');
  const [timeRemaining, setTimeRemaining] = useState(WORK_MINUTES * 60);
  const [isActive, setIsActive] = useState(false);
  const intervalRef = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    if (isActive) {
      intervalRef.current = setInterval(() => {
        setTimeRemaining(prev => prev - 1);
      }, 1000);
    } else if (intervalRef.current) {
      clearInterval(intervalRef.current);
    }
    return () => {
      if (intervalRef.current) clearInterval(intervalRef.current);
    };
  }, [isActive]);

  useEffect(() => {
    if (timeRemaining === 0) {
      completionSound?.play();
      setIsActive(false);
      const nextMode = mode === 'work' ? 'break' : 'work';
      setMode(nextMode);
      setTimeRemaining(nextMode === 'work' ? WORK_MINUTES * 60 : BREAK_MINUTES * 60);
    }
  }, [timeRemaining, mode]);

  const toggleTimer = () => setIsActive(!isActive);

  const resetTimer = () => {
    setIsActive(false);
    setMode('work');
    setTimeRemaining(WORK_MINUTES * 60);
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${String(mins).padStart(2, '0')}:${String(secs).padStart(2, '0')}`;
  };

  const totalDuration = mode === 'work' ? WORK_MINUTES * 60 : BREAK_MINUTES * 60;
  const progress = (totalDuration - timeRemaining) / totalDuration;
  const strokeDashoffset = 283 * (1 - progress);

  return (
    <div className="card-container max-w-md mx-auto flex flex-col items-center">
      <h2 className="text-xl font-semibold mb-6 text-white">Pomodoro Timer</h2>
      <div className="relative w-64 h-64">
        <svg className="w-full h-full" viewBox="0 0 100 100">
          {/* Background circle */}
          <circle
            className="text-neutral-800"
            strokeWidth="7"
            stroke="currentColor"
            fill="transparent"
            r="45"
            cx="50"
            cy="50"
          />
          {/* Progress circle */}
          <circle
            className="text-cyan-400"
            strokeWidth="7"
            strokeDasharray="283"
            strokeDashoffset={strokeDashoffset}
            strokeLinecap="round"
            stroke="currentColor"
            fill="transparent"
            r="45"
            cx="50"
            cy="50"
            style={{ transform: 'rotate(-90deg)', transformOrigin: 'center' }}
          />
        </svg>
        <div className="absolute inset-0 flex flex-col items-center justify-center">
          <p className="text-5xl font-bold text-white">{formatTime(timeRemaining)}</p>
          <p className="text-lg text-neutral-400 uppercase tracking-widest">{mode}</p>
        </div>
      </div>
      <div className="flex gap-4 mt-8">
        <button onClick={toggleTimer} className="btn-primary w-32">
          {isActive ? <Pause /> : <Play />}
          <span className="ml-2">{isActive ? 'Pause' : 'Start'}</span>
        </button>
        <button onClick={resetTimer} className="btn-ghost">
          <RotateCcw />
        </button>
      </div>
    </div>
  );
}
